import React from "react";
import "../App.css";
import List from "./list";

class Search extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            query: "",
            filteredData: [],
        };
        this.clickHandler = this.clickHandler.bind(this);
        this.getData = this.getData.bind(this);
    }
    clickHandler() {
        var that = this;
        var searchBox = document.getElementById("query");
        this.setState({ query: searchBox.value });
        if (this.state.query !== "") {
            this.setState({ filteredData: this.getData(this.state.query) });
        }
    }
    async getData(query) {
        console.log("getData is run");
        var that = this;
        var data = fetch("http://localhost:5000/api/getbyname/" + query)
            .then((response) => {
                return response.json();
            })
            .then((responseJSON) => {
                console.log(responseJSON);
                return responseJSON;
            });
        that.setState({ filteredData: data });
        return data;
    }

    render() {
        return (
            <div>
                <input type="text" id="query" placeholder="Search" />
                <button id="search-button" onClick={this.clickHandler}>
                    Search
                </button>
                <div>
                    <List data={this.state.filteredData}></List>
                </div>
            </div>
        );
    }
}

export default Search;
