import React from "react";
import "../App.css";

class Edit extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            Name: this.props.entry.Name,
            Platform: this.props.entry.Platform,
            Year: this.props.entry.Year,
            Global_Sales: this.props.entry.Global_Sales,
        };
    }

    updateData() {
        var updatedData = {
            Name: document.getElementById("name").value,
            Platform: document.getElementById("platform").value,
            Year: document.getElementById("year").value,
            Global_Sales: document.getElementById("global-sales").value,
        };

        fetch("http://localhost:5000/api/update", {
            method: "PUT",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify(updatedData),
        })
            .then((response) => response.json())
            .then((data) => {
                console.log("Updated data:\n" + JSON.stringify(data));
            })
            .catch((error) => {
                console.error("Error:", error);
            });
    }

    onChange(event) {
        var fieldName = event.target.id;
        this.setState({});
    }

    render() {
        return (
            <div style={{ maxWidth: "50%" }}>
                <form>
                    <input
                        className="editField"
                        id="Name"
                        type="text"
                        value={this.state.entry.Name}
                        onChange={this.onChange("Name")}
                    />
                    <input
                        className="editField"
                        id="Platform"
                        type="text"
                        value={this.state.entry.Platform}
                        onChange={this.onChange("Platform")}
                    />
                    <input
                        className="editField"
                        id="Year"
                        type="text"
                        value={this.state.entry.Year}
                        onChange={this.onChange("Year")}
                    />
                    <input
                        className="editField"
                        id="Global_Sales"
                        type="text"
                        value={this.state.entry.Global_Sales}
                        onChange={this.onChange("GLobal_Sales")}
                    />
                </form>
                <button
                    onclick={() => {
                        this.updateData();
                    }}
                >
                    Submit
                </button>
            </div>
        );
    }
}

export default Edit;
