import React from "react";
import ReactDOM from "react-dom";
import "../App.css";
import Alert from "./alert";

function Add() {
    function addData() {
        var name = document.getElementById("Name").value;
        var platform = document.getElementById("Platform").value;
        var year = document.getElementById("Year").value;
        var globalSales = document.getElementById("Global_Sales").value;

        var message;

        if (name === "" || platform === "" || year === "" || globalSales === "") {
            message = "Please fill all fields to submit data.";
            ReactDOM.render(<Alert alertText={message}></Alert>, document.getElementById("alert"));
        } else {
            var newEntry = {
                Name: name,
                Plaform: platform,
                Year: year,
                Global_Sales: globalSales,
            };

            message = "Data saved successfully!";

            fetch("http://localhost:5000/api/add", {
                method: "POST",
                headers: {
                    "Content-Type": "application/json",
                },
                body: JSON.stringify(newEntry),
            })
                .catch((error) => {
                    console.error("Error:", error);
                })
                .then((data) => {
                    console.log(data);
                })
                .then(() => {
                    ReactDOM.render(
                        <Alert alertText={message}></Alert>,
                        document.getElementById("alert")
                    );
                });
        }
    }

    return (
        <div>
            <form>
                <label for="Name">Name</label>
                <input id="Name" className="form-field" type="text"></input>
                <label for="Platform">Platform</label>
                <input id="Platform" className="form-field" type="text"></input>
                <label for="Year">Year</label>
                <input id="Year" className="form-field" type="text"></input>
                <label for="Global_Sales">Global Sales</label>
                <input id="Global_Sales" className="form-field" type="text"></input>
            </form>
            <button
                onClick={() => {
                    this.addData();
                }}
            >
                Submit
            </button>
            <div id="alert"></div>
        </div>
    );
}

export default Add;
