import React from "react";
import "../App.css";

function Edit(props) {
    this.props = props;

    /*     async function getData(id) {
        var url = "http://localhost:5000/api/get/" + id;
        var data = fetch(url)
            .catch((err) => {
                console.log(err);
            })
            .then((response) => {
                return response;
            });
        console.log("getData is run");
        console.log(data);
        return data;
    } */

    function updateData() {
        var updatedData = {
            _id: this.props.entry._id,
            Name: document.getElementById("Name").value,
            Platform: document.getElementById("Platform").value,
            Year: document.getElementById("Year").value,
            Global_Sales: document.getElementById("Global_Sales").value,
        };
        console.log("Updated data:\n" + JSON.stringify(updatedData));

        fetch("http://localhost:5000/api/update", {
            method: "PUT",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify(updatedData),
        })
            .then((response) => response.json())
            .then((data) => {
                console.log("Updated data:\n" + JSON.stringify(data));
            })
            .catch((error) => {
                console.error("Error:", error);
            });
    }
    return (
        <div style={{ maxWidth: "50%" }}>
            <form>
                <label className="form-field-label" htmlFor="Name">
                    Name
                </label>
                <input
                    className="form-field"
                    id="Name"
                    type="text"
                    defaultValue={this.props.entry.Name}
                />
                <label className="form-field-label" htmlFor="Platform">
                    Platform
                </label>
                <input
                    className="form-field"
                    id="Platform"
                    type="text"
                    defaultValue={this.props.entry.Platform}
                />
                <label className="form-field-label" htmlFor="Year">
                    Year
                </label>
                <input
                    className="form-field"
                    id="Year"
                    type="text"
                    defaultValue={this.props.entry.Year}
                />
                <label className="form-field-label" htmlFor="Global_Sales">
                    Global Sales
                </label>
                <input
                    className="form-field"
                    id="Global_Sales"
                    type="text"
                    defaultValue={this.props.entry.Global_Sales}
                />
            </form>
            <button
                className="submit-button"
                onClick={() => {
                    updateData();
                }}
            >
                Submit
            </button>
        </div>
    );
}

export default Edit;
