import React, { useState } from "react";
import { useParams } from "react-router-dom";
import "../App.css";

function Edit() {
    /* constructor(props) {
        super(props);
        this.state = {
            name: "",
            platform: "",
            year: "",
            globalSales: "",
        };
    } */

    var { params } = useParams();
    console.log(params.id);

    var defaultValues = getData(this.params.id);
    console.log(defaultValues);

    function getData(id) {
        var url = "http://localhost:5000/api/get/" + id;
        return fetch(url)
            .catch((err) => {
                console.log(err);
            })
            .then((response) => {
                return response;
            });
    }

    function updateData() {
        var updatedData = {
            _id: this.props.entry._id,
            Name: document.getElementById("Name").value,
            Platform: document.getElementById("Platform").value,
            Year: document.getElementById("Year").value,
            Global_Sales: document.getElementById("Global_Sales").value,
        };
        console.log("Updated data:\n" + JSON.stringify(updatedData));

        fetch("http://localhost:5000/api/update", {
            method: "PUT",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify(updatedData),
        })
            .then((response) => response.json())
            .then((data) => {
                console.log("Updated data:\n" + JSON.stringify(data));
            })
            .catch((error) => {
                console.error("Error:", error);
            });
    }
    return (
        <div style={{ maxWidth: "50%" }}>
            <form>
                <label className="form-field-label" for="Name">
                    Name
                </label>
                <input
                    className="form-field"
                    id="Name"
                    type="text"
                    defaultValue={defaultValues.Name}
                />
                <label className="form-field-label" for="Platform">
                    Platform
                </label>
                <input
                    className="form-field"
                    id="Platform"
                    type="text"
                    defaultValue={defaultValues.Platform}
                />
                <label className="form-field-label" for="Year">
                    Year
                </label>
                <input
                    className="form-field"
                    id="Year"
                    type="text"
                    defaultValue={defaultValues.Year}
                />
                <label className="form-field-label" for="Global_Sales">
                    Global Sales
                </label>
                <input
                    className="form-field"
                    id="Global_Sales"
                    type="text"
                    defaultValue={defaultValues.Global_Sales}
                />
            </form>
            <button
                className="submit-button"
                onClick={() => {
                    updateData();
                }}
            >
                Submit
            </button>
        </div>
    );
}

export default Edit;
