import React from "react";
import "../App.css";

function Alert(props) {
    if (props.status === "success") {
        return (
            <div className="alert-box" style={{ color: "green" }}>
                <p>{props.alertText}</p>
            </div>
        );
    } else {
        return (
            <div className="alert-box" style={{ color: "red" }}>
                <p>{props.alertText}</p>
            </div>
        );
    }
}

export default Alert;
