import React from "react";
import ReactDOM from "react-dom";
import { Link } from "react-router-dom";
import "../App.css";
import Alert from "./alert";

class Add extends React.Component {
    constructor(props) {
        super(props);
        this.state = {};
    }

    addData() {
        var name = document.getElementById("Name").value;
        var platform = document.getElementById("Platform").value;
        var year = document.getElementById("Year").value;
        var globalSales = document.getElementById("Global_Sales").value;

        var message;

        if (name === "" || platform === "" || year === "" || globalSales === "") {
            message = "Please fill all fields to submit data.";
            ReactDOM.render(
                <Alert alertText={message} status="error"></Alert>,
                document.getElementsByClassName("alert")[0]
            );
        } else {
            var newEntry = {
                Name: name,
                Plaform: platform,
                Year: year,
                Global_Sales: globalSales,
            };

            message = "Data saved successfully!";

            fetch("http://localhost:5000/api/add", {
                method: "POST",
                headers: {
                    "Content-Type": "application/json",
                },
                body: JSON.stringify(newEntry),
            })
                .catch((error) => {
                    console.error("Error:", error);
                    message = "Fetch error";
                    ReactDOM.render(
                        <Alert alertText={message} status="error"></Alert>,
                        document.getElementsByClassName("alert")[0]
                    );
                })
                .then((data) => {
                    console.log(data);
                })
                .then(() => {
                    ReactDOM.render(
                        <Alert alertText={message} status="success"></Alert>,
                        document.getElementsByClassName("alert")[0]
                    );
                });
        }
    }

    render() {
        return (
            <div>
                <form>
                    <label className="form-field-label" htmlFor="Name">
                        Name
                    </label>
                    <input id="Name" className="form-field" type="text"></input>
                    <label className="form-field-label" htmlFor="Platform">
                        Platform
                    </label>
                    <input id="Platform" className="form-field" type="text"></input>
                    <label className="form-field-label" htmlFor="Year">
                        Year
                    </label>
                    <input id="Year" className="form-field" type="text"></input>
                    <label className="form-field-label" htmlFor="Global_Sales">
                        Global Sales
                    </label>
                    <input id="Global_Sales" className="form-field" type="text"></input>
                </form>
                <button
                    className="submit-button"
                    onClick={() => {
                        this.addData();
                    }}
                >
                    Submit
                </button>
                <div className="alert"></div>
                <Link>
                    <button className="back-button">Back</button>
                </Link>
            </div>
        );
    }
}

export default Add;
