import React from "react";
import "../App.css";

class Edit extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            entry: this.props.entry,
        };
    }

    updateData() {
        var updatedData = {
            Name: document.getElementById("name").value,
            Platform: document.getElementById("platform").value,
            Year: document.getElementById("year").value,
            Global_Sales: document.getElementById("global-sales").value,
        };

        fetch("http://localhost:5000/api/update", {
            method: "PUT",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify(updatedData),
        })
            .then((response) => response.json())
            .then((data) => {
                console.log("Updated data:\n" + JSON.stringify(data));
            })
            .catch((error) => {
                console.error("Error:", error);
            });
    }

    render() {
        return (
            <div style={{ maxWidth: "50%" }}>
                <form>
                    <input className="" id="name" type="text" value={this.state.entry.Name} />
                    <input id="platform" type="text" value={this.state.entry.Platform} />
                    <input id="year" type="number" value={this.state.entry.Year} />
                    <input id="global-sales" type="number" value={this.state.entry.Global_Sales} />
                </form>
                <button
                    onclick={() => {
                        this.updateData();
                    }}
                ></button>
            </div>
        );
    }
}

export default Edit;
