import React from "react";
import ReactDOM from "react-dom";
import "../App.css";
import Alert from "./alert";

function Edit(props) {
    function updateData() {
        var name = document.getElementById("Name").value;
        var platform = document.getElementById("Platform").value;
        var year = document.getElementById("Year").value;
        var globalSales = document.getElementById("Global_Sales").value;

        var message;

        if (name === "" || platform === "" || year === "" || globalSales === "") {
            message = "Please fill all fields to submit data.";
            ReactDOM.render(<Alert alertText={message}></Alert>, document.getElementById("alert"));
        } else {
            var updatedData = {
                _id: props.entry._id,
                Name: name,
                Platform: platform,
                Year: year,
                Global_Sales: globalSales,
            };
            console.log("Updated data:\n" + JSON.stringify(updatedData));

            fetch("http://localhost:5000/api/update", {
                method: "PUT",
                headers: {
                    "Content-Type": "application/json",
                },
                body: JSON.stringify(updatedData),
            })
                .catch((error) => {
                    console.error("Error:", error);
                })
                .then((response) => response.json())
                .then((data) => {
                    console.log("Updated data:\n" + JSON.stringify(data));
                });
        }
    }
    return (
        <div style={{ maxWidth: "50%" }}>
            <form>
                <label className="form-field-label" htmlFor="Name">
                    Name
                </label>
                <input
                    className="form-field"
                    id="Name"
                    type="text"
                    defaultValue={props.entry.Name}
                />
                <label className="form-field-label" htmlFor="Platform">
                    Platform
                </label>
                <input
                    className="form-field"
                    id="Platform"
                    type="text"
                    defaultValue={props.entry.Platform}
                />
                <label className="form-field-label" htmlFor="Year">
                    Year
                </label>
                <input
                    className="form-field"
                    id="Year"
                    type="text"
                    defaultValue={props.entry.Year}
                />
                <label className="form-field-label" htmlFor="Global_Sales">
                    Global Sales
                </label>
                <input
                    className="form-field"
                    id="Global_Sales"
                    type="text"
                    defaultValue={props.entry.Global_Sales}
                />
            </form>
            <button
                className="submit-button"
                onClick={() => {
                    updateData();
                }}
            >
                Submit
            </button>
            <div className="alert"></div>
        </div>
    );
}

export default Edit;
