import React from "react";
import "../App.css";
import List from "./list";
import { ReactComponent } from "*.svg";

class Search extends ReactComponent {
    constructor(props) {
        super(props);
        this.state = { query: "" };
    }
    clickHandler() {
        const searchBox = document.getElementById("query");
        this.setState({ query: searchBox.value });
    }
    render() {
        return (
            <div>
                <form>
                    <input
                        type="text"
                        name="query"
                        id="query"
                        placeholder="Search"
                    ></input>
                    <button id="search-button" onClick={this.clickHandler}>
                        Search
                    </button>
                </form>
                <div>
                    <List></List>
                </div>
            </div>
        );
    }
}

export default Search;
