import React from "react";
import ReactDOM from "react-dom";
import "../App.css";
import { Link } from "react-router-dom";
import { Button, FormInput, Form, FormGroup } from "shards-react";
import Alert from "./alert";

class Add extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            isValid: "",
        };
        this.changeHandler = this.changeHandler.bind(this);
    }

    changeHandler(event) {
        if (event.target.value === "") {
            this.setState({ isValid: false });
        } else {
            this.setState({ isValid: true });
        }

        if (this.state.isValid === true) {
            event.target.setAttribute("valid", "true");
        } else if (this.state.isValid === false) {
            event.target.setAttribute("invalid", "true");
        } else {
            return;
        }
    }

    addData() {
        var name = document.getElementById("Name").value;
        var platform = document.getElementById("Platform").value;
        var year = document.getElementById("Year").value;
        var globalSales = document.getElementById("Global_Sales").value;

        var message;

        if (name === "" || platform === "" || year === "" || globalSales === "") {
            message = "Please fill all fields to submit data.";
            ReactDOM.render(
                <Alert alertText={message} status="error"></Alert>,
                document.getElementsByClassName("alert")[0]
            );
        } else {
            var newEntry = {
                Name: name,
                Platform: platform,
                Year: year,
                Global_Sales: globalSales,
            };

            message = "Data saved successfully!";

            fetch("https://node-mongo-games.herokuapp.com/api/add", {
                method: "POST",
                headers: {
                    "Content-Type": "application/json",
                },
                body: JSON.stringify(newEntry),
            })
                .catch((error) => {
                    console.error("Error:", error);
                    message = "Fetch error";
                    ReactDOM.render(
                        <Alert alertText={message} status="error"></Alert>,
                        document.getElementsByClassName("alert")[0]
                    );
                })
                .then((data) => {
                    console.log(data);
                })
                .then(() => {
                    ReactDOM.render(
                        <Alert alertText={message} status="success"></Alert>,
                        document.getElementsByClassName("alert")[0]
                    );
                });
        }
    }

    render() {
        return (
            <div className="add">
                <h3>Add a New Entry</h3>
                <Form>
                    <FormGroup>
                        <label htmlFor="Name">Name</label>
                        <FormInput
                            id="Name"
                            type="text"
                            onChange={(e) => {
                                this.changeHandler(e);
                            }}
                        ></FormInput>
                    </FormGroup>
                    <FormGroup>
                        <label htmlFor="Platform">Platform</label>
                        <FormInput
                            id="Platform"
                            type="text"
                            onChange={(e) => {
                                this.changeHandler(e);
                            }}
                        ></FormInput>
                    </FormGroup>
                    <FormGroup>
                        <label htmlFor="Year">Year</label>
                        <FormInput
                            id="Year"
                            type="text"
                            onChange={(e) => {
                                this.changeHandler(e);
                            }}
                        ></FormInput>
                    </FormGroup>
                    <FormGroup>
                        <label htmlFor="Global_Sales">Global Sales</label>
                        <FormInput
                            id="Global_Sales"
                            type="text"
                            onChange={(e) => {
                                this.changeHandler(e);
                            }}
                        ></FormInput>
                    </FormGroup>
                </Form>
                <Button
                    onClick={() => {
                        this.addData();
                    }}
                >
                    Submit
                </Button>
                <div className="alert"></div>
                <Link to="/">
                    <Button theme="secondary">Back to Search</Button>
                </Link>
            </div>
        );
    }
}

export default Add;
