import React from "react";
import ReactDOM from "react-dom";
import "../App.css";
import { Button, FormInput, Form, FormGroup } from "shards-react";
import Alert from "./alert";

function Edit(props) {
    function updateData() {
        var name = document.getElementById("Name").value;
        var platform = document.getElementById("Platform").value;
        var year = document.getElementById("Year").value;
        var globalSales = document.getElementById("Global_Sales").value;

        var message;

        if (name === "" || platform === "" || year === "" || globalSales === "") {
            message = "Please fill all fields to submit data.";
            ReactDOM.render(
                <Alert alertText={message} status="error"></Alert>,
                document.getElementsByClassName("alert")[0]
            );
        } else {
            message = "Edit successful!";

            var updatedData = {
                _id: props.entry._id,
                Name: name,
                Platform: platform,
                Year: year,
                Global_Sales: globalSales,
            };
            console.log("Updated data:\n" + JSON.stringify(updatedData));

            fetch("http://localhost:5000/api/update", {
                method: "PUT",
                headers: {
                    "Content-Type": "application/json",
                },
                body: JSON.stringify(updatedData),
            })
                .catch((error) => {
                    message = "Fetch error";
                    console.error("Error:", error);
                    ReactDOM.render(
                        <Alert alertText={message} status="error"></Alert>,
                        document.getElementsByClassName("alert")[0]
                    );
                })
                .then((response) => response.json())
                .then((data) => {
                    console.log("Updated data:\n" + JSON.stringify(data));
                    ReactDOM.render(
                        <Alert alertText={message} status="success"></Alert>,
                        document.getElementsByClassName("alert")[0]
                    );
                });
        }
    }
    return (
        <div className="edit">
            <Form>
                <FormGroup>
                    <label htmlFor="Name">Name</label>
                    <FormInput id="Name" type="text" defaultValue={props.entry.Name}></FormInput>
                </FormGroup>
                <FormGroup>
                    <label htmlFor="Platform">Platform</label>
                    <FormInput id="Platform" type="text" defaultValue={props.entry.Platform}></FormInput>
                </FormGroup>
                <FormGroup>
                    <label htmlFor="Year">Year</label>
                    <FormInput id="Year" type="text" defaultValue={props.entry.Year}></FormInput>
                </FormGroup>
                <FormGroup>
                    <label htmlFor="Global_Sales">Global Sales</label>
                    <FormInput id="Global_Sales" type="text" defaultValue={props.entry.Global_Sales}></FormInput>
                </FormGroup>
            </Form>
            <Button
                className="submit-button"
                onClick={() => {
                    updateData();
                }}
            >
                Submit
            </Button>
            <div className="alert"></div>
        </div>
    );
}

export default Edit;
