import React from "react";
import "../App.css";

class Edit extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            Name: this.props.entry.Name,
            Platform: this.props.entry.Platform,
            Year: this.props.entry.Year,
            Global_Sales: this.props.entry.Global_Sales,
        };
        this.handleChange = this.handleChange.bind(this);
    }

    updateData() {
        var updatedData = {
            Name: document.getElementById("Name").value,
            Platform: document.getElementById("Platform").value,
            Year: document.getElementById("Year").value,
            Global_Sales: document.getElementById("Global_Sales").value,
        };

        fetch("http://localhost:5000/api/update", {
            method: "PUT",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify(updatedData),
        })
            .then((response) => response.json())
            .then((data) => {
                console.log("Updated data:\n" + JSON.stringify(data));
            })
            .catch((error) => {
                console.error("Error:", error);
            });
    }

    handleChange() {
        if (document.getElementById("Name") === undefined) {
            return;
        } else {
            this.setState({
                Name: document.getElementById("Name").value,
                Platform: document.getElementById("Platform").value,
                Year: document.getElementById("Year").value,
                Global_Sales: document.getElementById("Global_Sales").value,
            });
        }
    }

    render() {
        return (
            <div style={{ maxWidth: "50%" }}>
                <form>
                    <input
                        className="editField"
                        id="Name"
                        type="text"
                        value={this.state.Name}
                        onChange={this.handleChange()}
                    />
                    <input
                        className="editField"
                        id="Platform"
                        type="text"
                        value={this.state.Platform}
                        onChange={this.handleChange()}
                    />
                    <input
                        className="editField"
                        id="Year"
                        type="text"
                        value={this.state.Year}
                        onChange={this.handleChange()}
                    />
                    <input
                        className="editField"
                        id="Global_Sales"
                        type="text"
                        value={this.state.Global_Sales}
                        onChange={this.handleChange()}
                    />
                </form>
                <button
                    onclick={() => {
                        this.updateData();
                    }}
                >
                    Submit
                </button>
            </div>
        );
    }
}

export default Edit;
